function registrationRequest(callback) {
  const BASE_URL = pm.collectionVariables.get('BASE_URL');
  const url = BASE_URL + '/users';
  //  
  const username = pm.variables.replaceIn('{{$randomFirstName}}').toLowerCase();
  const email = pm.variables.replaceIn('{{$randomEmail}}').toLowerCase();
  const password = pm.collectionVariables.get('passwordConduit');
  const requestStructure = {
    url,
    method: 'POST',
    header: {
      'Content-Type': 'application/json',
    },
    body: {
      mode: 'raw',
      raw: JSON.stringify({
        user: { username, email, password }
      })
    }
  };
  pm.sendRequest(requestStructure, (error, response) => {
    if (response && response.json().user) {
      const user = response.json().user;
      //   
      pm.collectionVariables.set('username', username);
      pm.collectionVariables.set('email', email);
      pm.collectionVariables.set('password', password);
      pm.collectionVariables.set('tokenConduit', user.token);
      console.log("Created user:", user);
      if (callback) {
        callback()
      }
    } else {
      console.log("User creation failed:", error);
    }
  });
}

