function createArticle() {
  const BASE_URL = pm.collectionVariables.get('BASE_URL');
  const url = BASE_URL + '/articles'
  const title = pm.variables.replaceIn('{{$randomLoremSentence}}')
  const description = pm.variables.replaceIn('{{$randomLoremSentence}}');
  const body = pm.variables.replaceIn('{{$randomLoremSentences}}');
  const tagList = ['talent'];
  console.log(tagList)
  const token = pm.collectionVariables.get('tokenConduit')

  const requestStructure = {
    url,
    method: 'POST',
    header: {
      'Content-Type': 'application/json',
      'Authorization': `Token ${token}`,
    },
    body: {
      mode: 'raw',
      raw: JSON.stringify({
        article: { title, description, body, tagList }
      })
    }
  };
  pm.sendRequest(requestStructure, (error, response) => {
    if (response && response.json().article) {
      const article = response.json().article;
      pm.collectionVariables.set('slugConduit', article.slug)
      pm.collectionVariables.set('articleTitleConduit', article.title)
      pm.collectionVariables.set('articleBodyConduit', article.body)
      pm.collectionVariables.set('articleDescriptionConduit', article.description)
      pm.collectionVariables.set('articleTagsConduit', article.tagList)
      console.log('Created article:', article)

    }
    else {
      console.log('Article creation failed:', error)
    }
  })

}

function createCommentRequest(callback) {
  const BASE_URL = pm.collectionVariables.get('BASE_URL');
  const SLUG = pm.collectionVariables.get('slugConduit'); 
  const token = pm.collectionVariables.get('tokenConduit');

  const url = `${BASE_URL}articles/${SLUG}/comments`;

  const bodyText = pm.variables.replaceIn('{{$randomLoremSentence}}');

  const requestStructure = {
    url,
    method: 'POST',
    header: {
      'Content-Type': 'application/json',
      'Authorization': `Token ${token}`,
    },
    body: {
      mode: 'raw',
      raw: JSON.stringify({
        comment: { body: bodyText }
      })
    }
  };

  pm.sendRequest(requestStructure, (error, response) => {
    if (response && response.json().comment) {
      const comment = response.json().comment;

      pm.collectionVariables.set('id', comment.id);
      pm.collectionVariables.set('commentBody', comment.body);

      console.log('Created comment:', comment);

      if (callback) callback();
    } else {
      console.log("Comment creation failed:", error);
    }
  });
}
